import React from "react";

window.onload = function any_function_name() {

    //Localizar elemento ul por Id
    var list = document.getElementById('my-list');
    //Localizar elemento button por Id
    var addBtn = document.getElementById('new-li');




    //Función que añade elemento li con texto
    function addItem(listElem, text) {

        //Crear elemento li
        var li = document.createElement('li');

        //Crear button indentado
        var intBtn = document.createElement('button');
        intBtn.innerHTML = '+';
        intBtn.setAttribute("contentEditable", "false");
        intBtn.setAttribute("id", "indent");
        listElem.appendChild(intBtn);

        //Crear texto editable
        var textNode = document.createTextNode(text);
        //Manda los elementos en la función
        listElem.appendChild(li).appendChild(textNode);

        var newLi = document.getElementById('indent');
        newLi.onclick = function () {
            intBtn
        }

    }



    // event listeners
    addBtn.addEventListener('click', function () {
        addItem(list, 'Escribe aquí tu lista');
    });


}//window.onload


class Lists extends React.Component {
    state = {
        user: null
    };




    render() {
        return (
            <div>
                <h1>I love lists!</h1>
                <ul contentEditable="true" id="my-list">
                    <li>Escribe aquí tu lista</li>

                </ul>

                <button id='new-li'>+</button>
            </div>
        );
    }
}


export default Lists;
